package My_250_plus_programs_for_practice.Progs_Arrays;

import java.util.Arrays;

public class Arrays_ex_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String[] Names = {"Jhansi", "Muthyala", "Kumar" , "Adam"};
		int[] numbers = {1, 2, 3, 4, 5, 6, 7, 8};
		byte[] bytes = {1,3,4, 5,6};
		short[] shortint = {2,4,5,5,6,6,7};
		float[] floatint = {22.3f, 45.66f,333.22f};//  possible 
		double[] duble1 = {22.443,4324.2323, 423423.324322};
		long[] longint = {2131313l,13123212323l, 423423324322l};
		char[] chartstr = {'a', 'b', 'd', 'e', 'r', 'y'};
		
		System.out.println("String Array : "+Arrays.toString(Names));
		System.out.println("Int Array : "+Arrays.toString(numbers));
		System.out.println("Bytes Array : "+Arrays.toString(bytes));
		System.out.println("Short Array : "+Arrays.toString(shortint));
		System.out.println("float Array : "+Arrays.toString(floatint));
		System.out.println("double Array : "+Arrays.toString(duble1));
		System.out.println("Long Array : "+Arrays.toString(longint));
		System.out.println("Char Array : "+Arrays.toString(chartstr));

		
		
		
		
		
		
	}

}
